package com.rohit;

     public class DatatypeExample {
    	 
    	 
         // PRIMITIVE DATA TYPES --> It is fix and not change value
    	 
	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
        //Datatype--> tells which type of variable using
		
		//First type= Byte type--> 1 byte=#8 bit, in binary format
		
		
		byte StudentAge = 15;
		byte StudentsMarks =85;
		System.out.println(StudentAge + " " + StudentsMarks);
		
		//short data type --> 2 byte of data = 16 bit
		
	            short height = 98;
	            System.out.println(height);
	    
	    //int data type--> 4 byte of data= 32 bits
	   
	    int weight=32;
	    System.out.println(weight);
	    
	    //long data type--> 8 byte data= 64 bits
	     long PhoneNo = 1234567L;
	    		 System.out.println(PhoneNo);
	    		 
	    // float data type-> 4 bites of data = 32 bits
	    		 float marks = 77.7f;
	    		 System.out.println(marks);
	    
	   // double data type -> 8 bytes data= 64 bits
	    		 double stocks= 88.9D;
	    		 System.out.println(stocks);
	  //Charector data type -> 2 bytes= 16 bits
	    		 char grade= 'B';
	    		 System.out.println(grade);
	   
	   // boolan data type -> 1 bit
	    		 boolean attendance = true;
	    		 System.out.println(attendance);
	    		 
	    		 //
	    		 
	    		 int x =25;
	    		 int y = x;
	    		 System.out.println(x);
	    		 System.out.println(y);
	    		  y =35;
	    		 System.out.println(x);
	    		 System.out.println(y);
	    		
	   // NON-PRIMITIVE DATA TYPES --> object like array and strings
	    		 
	    		// int[] n = {10,20,30,40};
	    		// int[] n1= n;
	    	//	System.out.println("Initial" + Arrays.(n));
	    		 
	         
	}

}
